void rsa_test(RSA* rsa)
{
    char *message = "This is a message";
    unsigned char from[RSA_size(rsa)];
    unsigned char result[RSA_size(rsa)];
    unsigned char to[RSA_size(rsa)];
    memset(from, 0, RSA_size(rsa));
    memset(result, 0, RSA_size(rsa));
    memset(to, 0, RSA_size(rsa));
    memcpy(from, message, RSA_size(rsa));
    print("From: %s\r\n", from);

    RSA_private_encrypt(sizeof(from), from, to, rsa, 0);
    RSA_public_decrypt(sizeof(to), to, result, rsa, 0);
    print("Result: %s\r\n", (char *)result);
}
